import React, {Component} from 'react'
import TodoDataService from "../../api/todo/TodoDataService";
import AuthenticationService from "./AuthenticationService";


class ListTodosComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            todos : [],
            message: ''


        }
        this.deleteTodoClicked = this.deleteTodoClicked.bind(this);
    }

    componentDidMount() {
        var username = AuthenticationService.getLoggedInUser() //janky way of getting our username but it will do
        TodoDataService.retrieveAllTodos(username)
            .then(response => {
                // console.log(response)
                this.setState({todos:response.data })
            })
    }

    deleteTodoClicked(id){
        var username = AuthenticationService.getLoggedInUser();
        // console.log(id + "" +username);
        TodoDataService.deleteTodo(username,id)
            .then(response => this.setState({message:`Delete todo ${id}`}))
    }

    render() {
        return (
            <div>
                 <h1>List Todos</h1>
                 <div className={"alert-success"}>{this.state.message}</div>
                 <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Target Date</th>
                                <th>IsCompleted?</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                            this.state.todos.map (
                                todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.targetDate.toString()}</td>
                                        <td>{todo.done.toString()}</td>
                                        <td><button className={"btn btn warning"} onClick={() =>this.deleteTodoClicked(todo.id)}>del</button></td>
                                    </tr>
                            )
                            }
                        </tbody>
                    </table>
                 </div>
            </div>
        )
    }
}

export default ListTodosComponent
